CREATE  OR REPLACE VIEW `vpersona` AS
	SELECT  
		P.* , Parr.nombre AS parroquia, 
        Mun.idmunicipio, Mun.nombre AS municipio, 
        Est.idestado, Est.nombre AS estado, U.id_usuario,
        U.usuario, U.intento_fallido, U.tiempo_sesion, 
        U.estatus AS estatus_usuario, T.idtipo_usuario , 
        T.nombre AS tipo_usuario , Tra.idtrabajador , Tra.fecha_ingreso, 
        Tra.condicion_trabajador , Tra.estatus AS estatus_trabajador ,
        D.iddepartamento , D.nombre AS departamento ,
		C.idcargo , C.nombre AS cargo 
	FROM tpersonas AS P

	INNER JOIN tusuario AS U 
		ON U.idpersona = P.idpersona

	INNER JOIN ttipo_usuario AS T 
		ON T.idtipo_usuario = U.idtipo_usuario

	LEFT JOIN ttrabajador AS Tra
		ON Tra.idpersona = P.idpersona
        
	LEFT JOIN tparroquia AS Parr
		ON Parr.idparroquia = P.idparroquia         	

	LEFT JOIN tmunicipio AS Mun
		ON Mun.idmunicipio = Parr.idmunicipio

	LEFT JOIN testado AS Est
		ON Est.idestado = Mun.idestado
        
	LEFT JOIN tcargo AS C
		ON C.idcargo = Tra.idcargo
        
	LEFT JOIN tdepartamento AS D
		ON D.iddepartamento = C.iddepartamento;
